mapper.insert:

  SELECT LAST_INSERT_ID()

insert into sys_user (username, password, icon, mobile, realName,
  email, nickName, note, loginIp, loginTime, sign
  createTime, updateTime, createdBy, updateBy, tenantId, version, status
  )
values (#{model.username}, #{model.password}, #{model.icon}, #{model.mobile}, #{model.realName},
#{model.email}, #{model.nickName}, #{model.note}, #{model.loginIp}, #{model.loginTime}, #{model.sign}
#{model.createTime}, #{model.updateTime}, #{model.createdBy}, #{model.updateBy}, #{model.tenantId}, #{model.version}, #{model.status}
)

mapper.update:
UPDATE sys_user
SET updateBy = #{model.id}, loginIp = #{model.loginIp}
WHERE id = #{model.id}
